import React, { useState } from "react";
import { useParams } from "react-router-dom";
//creo el componente funcional City que mostrará los detalles de una ciudad
//recibida por props en la ruta /ciudad/{ciudadId}
export default function City(props){

    const { ciudadId } = useParams();
    const[infoCity, setInfoCity] = useState({});

    fetch(`https://api.openweathermap.org/data/2.5/weather?id=${ciudadId}&appid=4ae2636d8dfbdc3044bede63951a019b`)
        .then((respuesta) => respuesta.json())
        .then((info) => {
                const objetoCiudad ={
                    temp: info.main.temp,
                    weather: info.weather[0].main,
                    wind: info.wind.speed,
                    clouds: info.clouds.all
                }
        })
        if(props.city){
            return(
                <div>
                    <h2>{props.city.name}</h2>
                    <div>
                        <div>Temperatura: {props.city.temp}</div>
                        <div>Clima: {props.city.weather}</div>
                        <div>Viento: {props.city.wind}</div>
                        <div>Nubosidad: {props.city.clouds}</div>
                    </div>
                </div>
            )
        } else{
            return(
            <div>
               <p>no se cargo las props city</p>
            </div>
            )
        }
}
